import React, { useState, useEffect } from "react";
import LogoutConfirmationModal from "../ModalsComponent/LogoutConfirmationModal";
import NewBlogPostModal from "../ModalsComponent/NewBlogPostModal";
import DeleteConfirmationModal from "../ModalsComponent/DeleteConfirmationModal";
import { Button, Modal } from "react-bootstrap";
import "./ContabilitateHomePage.css";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";

const BlogPost = ({ post, openDeleteModal, handleOpenUpdateModal, data }) => (
  <div className="w3-card-4 w3-margin w3-white" key={post.id}>
    <img
      src={post.imgSrc}
      alt={post.imgAlt}
      style={{ width: "100%", height: "100%" }}
    />
    <div className="w3-container">
      <p></p>
      <h3>
        <b>{post.title}</b>
      </h3>
      <h5>
        {post.subtitle}
        <span className="w3-opacity"></span>
      </h5>
    </div>

    <div className="w3-container">
      <p></p>
      <div dangerouslySetInnerHTML={{ __html: post.text }} />{" "}
      {/* Use dangerouslySetInnerHTML */}
      <div className="w3-row">
        <div className="w3-col m8 s12">
          <p></p>
          <p>
            <button
              className="w3-button w3-padding-large w3-white w3-border"
              onClick={() => window.open(post.buttonRedirectURL, "_blank")}
            >
              <b>{post.buttonText}</b>
            </button>
          </p>
        </div>
        {data.departamentAngajat === "Management" && (
          <div>
            <a
              className="deletePost"
              title="Delete"
              data-toggle="tooltip"
              onClick={() => openDeleteModal(post.id)}
            >
              <i className="material-icons">&#xE872;</i>
            </a>
            <a
              className="editPost"
              title="Edit"
              data-toggle="tooltip"
              onClick={() => handleOpenUpdateModal(post)}
            >
              <i className="material-icons">&#xE254;</i>
            </a>
          </div>
        )}
      </div>
    </div>
  </div>
);

const initialFormData = {
  imgSrc: "",
  imgAlt: "",
  title: "",
  subtitle: "",
  text: "",
  buttonText: "",
  buttonRedirectURL: "",
};

function ContabilitateHomePage() {
  const location = useLocation();
  const { numeAngajat, prenumeAngajat, departamentAngajat } =
    location.state || {};
  const navigate = useNavigate();
  const [showLogoutConfirmation, setShowLogoutConfirmation] = useState(false);
  const [showNewBlogPostModal, setShowNewBlogPostModal] = useState(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [data, setData] = useState([]);
  const [blogPostId, setBlogPostId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [employees, setEmployees] = useState([]);
  const [blogPosts, setBlogPosts] = useState([]);
  const [formData, setFormData] = useState(initialFormData);
  const [imgSrc, setImgSrc] = useState("");

  const [updateData, setUpdateData] = useState({
    ImgSrc: "",
    ImgAlt: "",
    Title: "",
    Subtitle: "",
    Text: "",
    ButtonText: "",
    ButtonRedirectURL: "",
  });

  const getAdminEmployees = () => {
    fetch("http://localhost:5036/api/DateCompleteAngajat")
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Failed to fetch data");
        }
      })
      .then((data) => {
        // Filter employees with isAdmin = true
        const isAdminEmployees = data.filter((employee) => employee.isAdmin);
        setEmployees(isAdminEmployees);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  const getBlogPosts = () => {
    fetch("http://localhost:5188/api/ResourcesBlogPosts")
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Failed to fetch data");
        }
      })
      .then((data) => {
        setBlogPosts(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  const openDeleteModal = (blogPostId) => {
    setBlogPostId(blogPostId);
    setShowDeleteConfirmation(true);
  };

  const closeDeleteModal = () => {
    setShowDeleteConfirmation(false);
  };

  const handleDelete = (blogPostId) => {
    const apiUrl = `http://localhost:5188/api/ResourcesBlogPosts/StergereBlogPost/${blogPostId}`;
    fetch(apiUrl, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          // Refresh the blog posts after successful deletion
          getBlogPosts();
          closeDeleteModal();
        } else {
          // Handle error case here, e.g., display an error message
          console.error("Failed to delete the blog post");
        }
      })
      .catch((error) => {
        // Handle any network errors
        console.error("Error deleting the blog post:", error);
      });
  };

  useEffect(() => {
    getBlogPosts();
  }, []);

  useEffect(() => {
    getAdminEmployees();
  }, []);

  const handleLogout = () => {
    setShowLogoutConfirmation(true);
  };

  const handleConfirmLogOut = () => {
    setShowLogoutConfirmation(false);
    navigate("/");
  };

  const handleCancelLogOut = () => {
    setShowLogoutConfirmation(false);
  };

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleShowNewBlogPostModal = () => {
    setShowNewBlogPostModal(true);
  };

  const handleCloseNewBlogPostModal = () => {
    setShowNewBlogPostModal(false);
  };

  const handleOpenUpdateModal = (blogPostData) => {
    setShowUpdateModal(true);
    setUpdateData(blogPostData);
  };

  const handleImageChange = (e) => {
    const image = e.target.files[0];
    setSelectedImage(image);
  };

  const handleUpdate = (e) => {
    e.preventDefault();

    // Create a variable to store the updated data
    const updatedData = { ...updateData };

    if (selectedImage) {
      const formDataUpload = new FormData();
      formDataUpload.append("image", selectedImage);

      fetch(
        "http://localhost:5188/api/ResourcesBlogPosts/ManageUploadedImages",
        {
          method: "POST",
          body: formDataUpload,
        }
      )
        .then((response) => response.json())
        .then((data) => {
          const newImgSrc = data.imagePath;
          updatedData.ImgSrc = newImgSrc; // Update ImgSrc with the response

          // Continue with the update logic
          performUpdate(updatedData);
        })
        .catch((error) => {
          console.error("Image upload error:", error);
          // Handle image upload error
        });
    } else {
      // If no image is selected, directly update the blog post
      performUpdate(updatedData);
    }
  };

  // Function to perform the blog post update
  const performUpdate = (dataToUpdate) => {
    fetch(
      `http://localhost:5188/api/ResourcesBlogPosts/UpdateBlogPost/${dataToUpdate.id}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dataToUpdate),
      }
    )
      .then((response) => {
        if (response.ok) {
          getBlogPosts();
          setShowUpdateModal(false); // Close the modal
        } else {
          console.error("Error updating blog post with the Id:", blogPostId);
        }
      })
      .catch((error) => console.error("Error making PUT request:", error));
  };

  return (
    <div>
      <div>
        <nav id="navbar">
          <div className="wrapper">
            <div className="logo">
              <a
                href="/adminHomePage"
                onClick={(e) => {
                  e.preventDefault();
                  navigate("/adminHomePage", {
                    state: {
                      numeAngajat: numeAngajat,
                      prenumeAngajat: prenumeAngajat,
                    },
                  });
                }}
              >
                <img
                  src="https://logos-world.net/wp-content/uploads/2022/07/Hertz-Logo.png"
                  alt="Hertz Logo"
                  style={{ maxWidth: "100px", maxHeight: "50px" }}
                />
              </a>
            </div>
            <span className="welcome-label" style={{ marginLeft: "-240px" }}>
              Welcome back, {numeAngajat} {prenumeAngajat}!
            </span>
            <input type="radio" name="slider" id="menu-btn" />
            <input type="radio" name="slider" id="close-btn" />
            <ul className="nav-links">
              <label htmlFor="close-btn" className="btn close-btn">
                <i className="fas fa-times"></i>
              </label>
              <li>
                <a
                  href="/angajatPage"
                  onClick={(e) => {
                    e.preventDefault(); // Prevent the default link behavior
                    navigate("/providerPage", {
                      state: {
                        numeAngajat: numeAngajat,
                        prenumeAngajat: prenumeAngajat,
                      },
                    });
                  }}
                >
                  Employees
                </a>
              </li>
              <li>
                <a
                  href="/providerPage"
                  onClick={(e) => {
                    e.preventDefault(); // Prevent the default link behavior
                    navigate("/providerPage", {
                      state: {
                        from: {
                          pathname: "/contabilitateHomePage",
                        },
                        numeAngajat: numeAngajat,
                        prenumeAngajat: prenumeAngajat,
                      },
                    });
                  }}
                >
                  Providers
                </a>
              </li>
              <li>
                <a
                  href="/angajatPage"
                  onClick={(e) => {
                    e.preventDefault(); // Prevent the default link behavior
                    navigate("/stocPage", {
                      state: {
                        numeAngajat: numeAngajat,
                        prenumeAngajat: prenumeAngajat,
                      },
                    });
                  }}
                >
                  Stock
                </a>
              </li>
              <input type="checkbox" id="showMega" />
              <li>
                <a className="LogoutButton" onClick={handleLogout}>
                  <i className="fas fa-sign-out-alt"></i> Log Out
                </a>
              </li>
            </ul>
            <label htmlFor="menu-btn" className="btn menu-btn">
              <i className="fas fa-bars"></i>
            </label>
          </div>
        </nav>

        <LogoutConfirmationModal
          show={showLogoutConfirmation}
          onHide={handleCancelLogOut}
          onConfirmLogout={handleConfirmLogOut}
        />

        <NewBlogPostModal
          show={showNewBlogPostModal}
          handleClose={handleCloseNewBlogPostModal}
        />

        <DeleteConfirmationModal
          show={showDeleteConfirmation}
          onHide={closeDeleteModal}
          onConfirmDelete={handleDelete}
          blogPostId={blogPostId}
        />

        {/* Update Modal */}
        <Modal
          show={showUpdateModal}
          onHide={() => setShowUpdateModal(false)}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Actualizare Blog Post</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={handleUpdate}>
              <div className="form-group mt-3">
                <label>Upload New Image:</label>
                <p></p>
                <input
                  type="file"
                  accept=".jpg, .jpeg, .png, .gif"
                  onChange={handleImageChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="Title">Title:</label>
                <input
                  type="text"
                  className="form-control"
                  id="Title"
                  name="Title"
                  value={updateData.title}
                  onChange={(e) =>
                    setUpdateData({ ...updateData, title: e.target.value })
                  }
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="Subtitle">Subtitle:</label>
                <input
                  type="text"
                  className="form-control"
                  id="Subtitle"
                  name="Subtitle"
                  value={updateData.subtitle}
                  onChange={(e) =>
                    setUpdateData({ ...updateData, subtitle: e.target.value })
                  }
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="Text">Text:</label>
                <input
                  type="text"
                  className="form-control"
                  id="Text"
                  name="Text"
                  value={updateData.text}
                  onChange={(e) =>
                    setUpdateData({ ...updateData, text: e.target.value })
                  }
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="ButtonText">Button Text:</label>
                <input
                  type="text"
                  className="form-control"
                  id="ButtonText"
                  name="ButtonText"
                  value={updateData.buttonText}
                  onChange={(e) =>
                    setUpdateData({ ...updateData, buttonText: e.target.value })
                  }
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="ButtonRedirectURL">Button Redirect URL:</label>
                <input
                  type="text"
                  className="form-control"
                  id="ButtonRedirectURL"
                  name="ButtonRedirectURL"
                  value={updateData.buttonRedirectURL}
                  onChange={(e) =>
                    setUpdateData({
                      ...updateData,
                      buttonRedirectURL: e.target.value,
                    })
                  }
                  required
                />
              </div>

              <button type="submit" className="btn btn-success mt-4">
                Actualizare
              </button>
            </form>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => setShowUpdateModal(false)}
            >
              Inchidere
            </Button>
          </Modal.Footer>
        </Modal>
      </div>

      <div className="w3-content" style={{ maxWidth: "1400px" }}>
        <header className="w3-container w3-center w3-padding-32">
          <h1 style={{ marginTop: "60px" }}>
            <b>Hertz</b>
          </h1>
          <p>Welcome to your work space. We are glad that you are here.</p>
        </header>
        <div className="w3-row">
          <div className="w3-col l8 s12">
            {/* Blog entries */}
            {/* Blog entry content */}
            {/* Blog entry content is postes dynamically*/}

            {blogPosts.map((post) => (
              <BlogPost
                post={post}
                key={post.id}
                openDeleteModal={openDeleteModal}
                handleOpenUpdateModal={handleOpenUpdateModal}
                data={{ numeAngajat, prenumeAngajat, departamentAngajat }}
              />
            ))}
          </div>
          <div className="w3-col l4">
            {/* Introduction menu */}
            {/* Introduction 1 */}
            <div className="w3-card w3-margin w3-margin-top">
              <img
                src="/images/Bajdechi Leonardo Picture.jpg"
                style={{ width: "100%" }}
                alt="Profile Image"
              />
              <div className="w3-container w3-white">
                <p></p>
                <h4>
                  <b>Leonardo Bajdechi</b>
                </h4>
                <p>
                  Software developer and creator of our work environment - the
                  Hertz App. He is the reason that all your tasks and resources
                  are organized and protected, within an easy-to-use
                  application, dedicated to everyone and with one of the biggest
                  communities in the car industry. He loves working in a team,
                  having fun with everyone, learning every day, and helping
                  others with anything they need.
                </p>
              </div>
              <p></p>
            </div>
            <hr />
            {/* Meet the team */}
            <div className="w3-card w3-margin">
              <div className="w3-container w3-padding">
                <h4>Yourself</h4>
              </div>
              <ul className="w3-ul w3-hoverable w3-white">
                {employees.map((employee, index) => (
                  <li className="w3-padding-16" key={index}>
                    <img
                      src="/images/Bajdechi Leonardo Picture.jpg"
                      alt={`${employee.nume} ${employee.prenume}`}
                      className="w3-left w3-margin-right"
                      style={{ width: "50px" }}
                    />
                    <span className="w3-large">{`${employee.nume} ${employee.prenume}`}</span>
                    <br />
                    <span>{employee.pozitie}</span>
                  </li>
                ))}
              </ul>
            </div>
            {/* Labels / tags */}
            <div class="w3-card w3-margin">
              <div class="w3-container w3-padding">
                <h4>Tags</h4>
              </div>
              <div class="w3-container w3-white">
                <p>
                  <span class="w3-tag w3-black w3-margin-bottom">Have fun</span>{" "}
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Relax
                  </span>{" "}
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Love
                  </span>
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Learn
                  </span>{" "}
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Study
                  </span>{" "}
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Cars
                  </span>
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Communicate
                  </span>{" "}
                  <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">
                    Games
                  </span>
                </p>
              </div>
            </div>
          </div>
        </div>
        <footer className="w3-container w3-dark-grey w3-padding-32 w3-margin-top">
          <a
            href="https://www.facebook.com/hertzro"
            target="_blank"
            rel="noopener noreferrer"
          >
            <label className="w3-button w3-blue w3-padding-large w3-margin-bottom">
              Follow us on <i className="fa fa-facebook"></i>
            </label>
          </a>
          <a
            href="https://www.instagram.com/hertz.romania/"
            target="_blank"
            rel="noopener noreferrer"
          >
            <label
              className="w3-button w3-padding-large w3-margin-bottom"
              style={{ backgroundColor: "#405DE6", color: "white" }}
            >
              Follow us on{" "}
              <i className="fa fa-instagram" style={{ color: "white" }}></i>
            </label>
          </a>
          <button className="w3-button w3-black w3-padding-large w3-margin-bottom">
            Team meetings coming soon. Stay tuned for many events.
          </button>
        </footer>
      </div>
    </div>
  );
}

export default ContabilitateHomePage;
