import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./ProviderPage.css";
import { Button, Modal } from "react-bootstrap";
import Navbar from "../NavbarComponent/Navbar";
import LogoutConfirmationModal from "../ModalsComponent/LogoutConfirmationModal";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";

function ProviderPage() {
  const location = useLocation();
  const { numeAngajat, prenumeAngajat } = location.state || {};
  const navigate = useNavigate();
  const [show, setShow] = useState(false);
  const [data, setData] = useState([]);
  const [showLogoutConfirmation, setShowLogoutConfirmation] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedTransmision, setSelectedTransmision] = useState("");

  const handleChangeTransmision = (event) => {
    setSelectedTransmision(event.target.value);
  };

  const [updateData, setUpdateData] = useState({
    furnizor: "",
    cui: "",
    regComertului: "",
    adresa: "",
    servicii: "",
    valabilitateaContractului: "",
  });

  const [formData, setFormData] = useState({
    furnizor: "",
    cui: "",
    regComertului: "",
    adresa: "",
    servicii: "",
    valabilitateaContractului: "",
  });

  const handleClose = () => {
    setShow(false);
    setFormData({
      furnizor: "",
      cui: "",
      regComertului: "",
      adresa: "",
      servicii: "",
      valabilitateaContractului: "",
    });
  };

  const [deleteProviderCui, setDeleteProviderCui] = useState("");
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [searchProvider, setSearchProvider] = useState("");
  const [searchCUI, setSearchCUI] = useState("");
  const [searchTR, setSearchTR] = useState("");

  const handleShow = () => setShow(true);

  const handleLogout = () => {
    setShowLogoutConfirmation(true);
  };

  const handleConfirmLogOut = () => {
    setShowLogoutConfirmation(false);
    navigate("/");
  };

  const handleCancelLogOut = () => {
    setShowLogoutConfirmation(false);
  };

  const handleSearchProviderInputChange = (e) => {
    setSearchProvider(e.target.value);
  };

  const handleSearchCUIChange = (e) => {
    setSearchCUI(e.target.value);
  };

  const handleSearchTRInputChange = (e) => {
    setSearchTR(e.target.value);
  };

  let filteredData;

  if (searchProvider) {
    filteredData = data.filter((provider) =>
      provider.marca.includes(searchProvider)
    );
  } else if (searchCUI) {
    filteredData = data.filter((provider) =>
      provider.cutieViteze.includes(searchCUI)
    );
  } else if (searchTR) {
    filteredData = data.filter((provider) => provider.includes(searchTR));
  } else {
    filteredData = data;
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Make a POST request to your API endpoint
    fetch(
      "http://localhost:5294/api/DateCompleteFurnizor/InserareDateFurnizor",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      }
    )
      .then((response) => {
        if (response.ok) {
          // Refresh the employee list after successful POST
          fetch(
            "http://localhost:5294/api/DateCompleteFurnizor/PreluareDateFurnizor"
          )
            .then((response) => response.json())
            .then((updatedData) => {
              setData(updatedData);
              handleClose();
            })
            .catch((error) =>
              console.error("Error fetching updated data:", error)
            );
        } else {
          // Handle errors here
        }
      })
      .catch((error) => console.error("Error making POST request:", error));
  };

  useEffect(() => {
    fetch("http://localhost:5294/api/DateCompleteFurnizor/PreluareDateFurnizor")
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleDeleteConfirmation = (deleteProviderCui) => {
    setDeleteProviderCui(deleteProviderCui);
    setShowDeleteConfirmation(true);
  };

  const handleOpenUpdateModal = (stocData) => {
    setShowUpdateModal(true);
    setUpdateData(stocData);
  };

  const handleDelete = () => {
    fetch(
      `http://localhost:5294/api/DateCompleteFurnizor/StergereDateFurnizor/${deleteProviderCui}`,
      {
        method: "DELETE",
      }
    )
      .then((response) => {
        if (response.ok) {
          fetch(
            "http://localhost:5294/api/DateCompleteFurnizor/PreluareDateFurnizor"
          )
            .then((response) => response.json())
            .then((updatedData) => setData(updatedData))
            .catch((error) =>
              console.error("Error fetching updated data:", error)
            );
          setShowDeleteConfirmation(false); // Close the delete confirmation modal
        } else {
          // Handle errors here
          console.error("Error deleting provider with CUI:", deleteProviderCui);
        }
      })
      .catch((error) => console.error("Error making DELETE request:", error));
  };

  const handleUpdate = (e) => {
    e.preventDefault();

    fetch(
      `http://localhost:5294/api/DateCompleteFurnizor/UpdateDateFurnizor?CUI=${updateData.cui}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updateData),
      }
    )
      .then((response) => {
        if (response.ok) {
          fetch(
            "http://localhost:5294/api/DateCompleteFurnizor/PreluareDateFurnizor"
          )
            .then((response) => response.json())
            .then((updatedData) => {
              setData(updatedData);
              setShowUpdateModal(false);
            })
            .catch((error) =>
              console.error("Error fetching updated data:", error)
            );
        } else {
          console.error("Error updating provider with CUI:", updateData.cnp);
        }
      })
      .catch((error) => console.error("Error making PUT request:", error));
  };

  return (
    <div>
      <nav id="navbar">
        <div className="wrapper">
          <div className="logo">
            <a
              href="/adminHomePage"
              onClick={(e) => {
                e.preventDefault();
                navigate("/adminHomePage", {
                  state: {
                    numeAngajat: numeAngajat,
                    prenumeAngajat: prenumeAngajat,
                  },
                });
              }}
            >
              <img
                src="https://logos-world.net/wp-content/uploads/2022/07/Hertz-Logo.png"
                alt="Hertz Logo"
                style={{ maxWidth: "100px", maxHeight: "50px" }}
              />
            </a>
          </div>
          <span className="welcome-label">
            Welcome back, {numeAngajat} {prenumeAngajat}!
          </span>
          <input type="radio" name="slider" id="menu-btn" />
          <input type="radio" name="slider" id="close-btn" />
          <ul className="nav-links">
            <label htmlFor="close-btn" className="btn close-btn">
              <i className="fas fa-times"></i>
            </label>
            <li>
              <a
                href="/angajatPage"
                onClick={(e) => {
                  e.preventDefault(); // Prevent the default link behavior
                  navigate("/angajatPage", {
                    state: {
                      numeAngajat: numeAngajat,
                      prenumeAngajat: prenumeAngajat,
                    },
                  });
                }}
              >
                Employees
              </a>
            </li>
            <li>
              <a
                href="/angajatPage"
                onClick={(e) => {
                  e.preventDefault(); // Prevent the default link behavior
                  navigate("/stocPage", {
                    state: {
                      numeAngajat: numeAngajat,
                      prenumeAngajat: prenumeAngajat,
                    },
                  });
                }}
              >
                Stock
              </a>
            </li>
            <input type="checkbox" id="showMega" />
            <li>
              <a href="">Providers</a>
            </li>
            <li>
              <a className="LogoutButton" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> Log Out
              </a>
            </li>
          </ul>
          <label htmlFor="menu-btn" className="btn menu-btn">
            <i className="fas fa-bars"></i>
          </label>
        </div>
      </nav>
      <LogoutConfirmationModal
        show={showLogoutConfirmation}
        onHide={handleCancelLogOut}
        onConfirmLogout={handleConfirmLogOut}
      />

      <div className="container" style={{ marginTop: "160px" }}>
        <div className="crud shadow-lg p-3 mb-5 mt-5 bg-body rounded">
          <div className="row">
            <div className="col-sm-3 mt-5 mb-4 text-gred">
              <div className="search">
                <form className="form-inline">
                  <div className="mb-2">
                    <input
                      className="form-control"
                      type="search"
                      placeholder="Provider"
                      aria-label="Search"
                      value={searchProvider}
                      onChange={handleSearchProviderInputChange}
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      className="form-control"
                      type="search"
                      placeholder="CUI"
                      aria-label="Search"
                      value={searchCUI}
                      onChange={handleSearchCUIChange}
                    />
                  </div>
                  <div className="mb-2">
                    <input
                      className="form-control"
                      type="search"
                      placeholder="Trade Register"
                      aria-label="Search"
                      value={searchTR}
                      onChange={handleSearchTRInputChange}
                    />
                  </div>
                </form>
              </div>
            </div>
            <div className="col-sm-3 offset-sm-2 mt-5 mb-4 text-gred">
              <h2 style={{ color: "black" }}>
                <b>Providers</b>
              </h2>
            </div>
            <div className="col-sm-3 offset-sm-1 mt-5 mb-4 text-gred">
              <Button variant="primary" onClick={handleShow}>
                Add providers
              </Button>
            </div>
          </div>
          <div className="row">
            <div className="table-responsive">
              <table className="table table-striped table-hover table-bordered custom-table">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Provider</th>
                    <th>CUI</th>
                    <th>Trade Register</th>
                    <th>Address</th>
                    <th>Services</th>
                    <th>End of Contract</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.map((provider) => (
                    <tr key={provider.id}>
                      <td>{provider.id}</td>
                      <td>{provider.furnizor}</td>
                      <td>{provider.cui}</td>
                      <td>{provider.regComertului}</td>
                      <td>{provider.adresa}</td>
                      <td>{provider.servicii}</td>
                      <td>{provider.valabilitateaContractului}</td>
                      <td>
                        <a
                          className="edit"
                          title="Edit"
                          data-toggle="tooltip"
                          onClick={() => handleOpenUpdateModal(provider)}
                        >
                          <i className="material-icons">&#xE254;</i>
                        </a>
                        <a
                          className="delete"
                          title="Delete"
                          data-toggle="tooltip"
                          onClick={() => handleDeleteConfirmation(provider.cui)}
                        >
                          <i className="material-icons">&#xE872;</i>
                        </a>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Delete Confirmation Modal */}
          <Modal
            show={showDeleteConfirmation}
            onHide={() => setShowDeleteConfirmation(false)}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>
                Are you sure you want to delete the next entry? This action is
                irreversible.
              </Modal.Title>
            </Modal.Header>
            <Modal.Footer>
              <Button
                variant="secondary"
                className="btn-modal"
                onClick={() => setShowDeleteConfirmation(false)}
              >
                No
              </Button>
              <Button variant="danger" onClick={handleDelete}>
                Yes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>

        <div>
          {/* Update Modal */}
          <Modal
            show={showUpdateModal}
            onHide={() => setShowUpdateModal(false)}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>Update Provider Values</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={handleUpdate}>
                <div className="form-group">
                  <label htmlFor="provider">Provider:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="provider"
                    name="provider"
                    value={updateData.furnizor}
                    onChange={(e) =>
                      setUpdateData({ ...updateData, furnizor: e.target.value })
                    }
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="cui">CUI:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="cui"
                    name="cui"
                    value={updateData.cui}
                    onChange={(e) =>
                      setUpdateData({ ...updateData, cui: e.target.value })
                    }
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="rc">Trade Register Code:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="rc"
                    name="rc"
                    value={updateData.regComertului}
                    onChange={(e) =>
                      setUpdateData({
                        ...updateData,
                        regComertului: e.target.value,
                      })
                    }
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="address">Address:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="address"
                    name="address"
                    value={updateData.adresa}
                    onChange={(e) =>
                      setUpdateData({
                        ...updateData,
                        adresa: e.target.value,
                      })
                    }
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="services">Services:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="services"
                    name="services"
                    value={updateData.servicii}
                    onChange={(e) =>
                      setUpdateData({
                        ...updateData,
                        servicii: e.target.value,
                      })
                    }
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="eoc">End of Contract:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="eoc"
                    name="eoc"
                    value={updateData.valabilitateaContractului}
                    onChange={(e) =>
                      setUpdateData({
                        ...updateData,
                        valabilitateaContractului: e.target.value,
                      })
                    }
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success mt-4">
                  Update data
                </button>
              </form>
            </Modal.Body>
            <Modal.Footer>
              <Button
                variant="secondary"
                onClick={() => setShowUpdateModal(false)}
              >
                Close
              </Button>
            </Modal.Footer>
          </Modal>
        </div>

        {/* <!-- Model Box --> */}
        <div className="model_box">
          <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>Add Provider</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    name="furnizor"
                    value={formData.furnizor}
                    onChange={handleChange}
                    placeholder="Provider"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="cui"
                    value={formData.cui}
                    onChange={handleChange}
                    placeholder="CUI"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="regComertului"
                    value={formData.regComertului}
                    onChange={handleChange}
                    placeholder="Trade Register Code"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="adresa"
                    value={formData.adresa}
                    onChange={handleChange}
                    placeholder="Address"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="servicii"
                    value={formData.servicii}
                    onChange={handleChange}
                    placeholder="Services"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="valabilitateaContractului"
                    value={formData.valabilitateaContractului}
                    onChange={handleChange}
                    placeholder="End of Contract"
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success mt-4">
                  Add
                </button>
              </form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Cancel
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      </div>
    </div>
  );
}

export default ProviderPage;
