import React, { useState } from "react";
import "./LoginPage.css";
import { useNavigate } from 'react-router-dom'; // Use useNavigate for navigation

function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [selectedDepartment, setSelectedDepartment] = useState("Management"); // Default to Management
  const [isErrorModalOpen, setIsErrorModalOpen] = useState(false); // State for the error modal
  const navigate = useNavigate(); // Get the navigate function for navigation

  // Function to open the error modal
  const openErrorModal = () => {
    setIsErrorModalOpen(true);
  };

  // Function to handle username input
  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  // Function to handle password input
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  // Function to handle dropdown selection
  const handleDropdownChange = (event) => {
    setSelectedDepartment(event.target.value);
  };

  // Function to handle form submission
  const handleFormSubmit = async (event) => {
    event.preventDefault();

    // Create an object with the data to send to the backend
    const requestData = {
      username,
      password,
      IsAdmin: selectedDepartment === "Management",
      IsAgentVanzari: selectedDepartment === "Vanzari",
      IsLucratorService: selectedDepartment === "Service",
      IsContabil: selectedDepartment === "Contabilitate",
    };

    // Send the requestData object to your backend using fetch or any other HTTP client library

    try {
      const response = await fetch("http://localhost:5036/api/Login/VerificareDateAutentificare", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestData),
      });

      if (response.ok) {
        // Authentication successful
        // Redirect to the appropriate page based on the department
        if (selectedDepartment === "Management") {
          navigate("/adminPage");
        } else if (selectedDepartment === "Vanzari") {
          navigate("/vanzariPage"); // Redirect to the Vanzari page
        } else if (selectedDepartment === "Service") {
          navigate("/servicePage"); // Redirect to the Service page
        } else if (selectedDepartment === "Contabilitate") {
          navigate("/contabilitatePage"); // Redirect to the Contabilitate page
        }
      } else {
        // Authentication failed
        openErrorModal(); // Call the function to open the error modal
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  };

  return (
    <div>
      {isErrorModalOpen && (
        <div className="modal">
          {/* Your modal content */}
          <h1>Authentication Failed</h1>
          <p>Please check your credentials and try again.</p>
          <button onClick={() => setIsErrorModalOpen(false)}>Close</button>
        </div>
      )}

      <div className="container">
        <div className="screen">
          <div className="screen__content">
            <form className="login" onSubmit={handleFormSubmit}>
              {/* ... (rest of your form code) */}
            </form>
          </div>
          <div className="screen__background">
            <span className="screen__background__shape screen__background__shape4"></span>
            <span className="screen__background__shape screen__background__shape2"></span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginForm;
