import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

function NewBlogPostModal({ show, handleClose, setFormData, setData, formData, handleChange }) {
  const handleImageFileChange = (e) => {
    // Ensure that the event object is passed as a parameter
    const file = e.target.files[0];
    if (file) {
      // Assuming you have a state variable named 'formData' and a corresponding 'setFormData' function
      setFormData({
        ...formData,
        imageFile: file,
      });
    }
  };
  const handleImageUploadClick = async () => {
    if (formData.imageFile) {
      const formDataToUpload = new FormData();
  
      // Append the image file with the appropriate field name and content type
      formDataToUpload.append('imageFile', formData.imageFile, formData.imageFile.name);
      console.log(formData.imageFile);
      console.log(formDataToUpload);
      try {
        const response = await fetch('http://localhost:5188/api/ResourcesBlogPosts/ManageUploadedImages', {
          method: 'POST',
          body: formDataToUpload,
          headers: {
            'Accept': 'application/json',
            // Set the content type based on the file type
            'Content-Type': formData.imageFile.type,
          },
        });
  
        if (response.ok) {
          const data = await response.json();
          const uploadedImagePath = data.imagePath;
  
          // Set the uploaded image path in the formData state
          setFormData({
            ...formData,
            imgSrc: uploadedImagePath,
          });
        } else {
          // Handle errors
        }
      } catch (error) {
        console.error('Error uploading image:', error);
      }
    }
  };
  
      

    const handleSubmit = (e) => {
        e.preventDefault();
        // Define your API endpoint
        const apiUrl = "http://localhost:5188/api/ResourcesBlogPosts/InserareNewBlogPost"; // Replace with your actual endpoint
      
        // Prepare the request body based on your data structure
        const requestData = {
          imgSrc: formData.imgSrc,
          imgAlt: formData.imgAlt,
          title: formData.title,
          subtitle: formData.subtitle,
          text: formData.text,
          buttonText: formData.buttonText,
          buttonRedirectURL: formData.buttonRedirectURL,
        };
      
        // Make a POST request to your API endpoint
        fetch(apiUrl, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(requestData),
        })
          .then((response) => {
            if (response.ok) {
              // Refresh the data or perform other actions after a successful POST
              fetch("http://localhost:5188/api/ResourcesBlogPosts")
                .then((response) => response.json())
                .then((updatedData) => {
                  setData(updatedData);
                  handleClose();
                })
                .catch((error) => console.error("Error fetching updated data:", error));
            } else {
              // Handle errors here
            }
          })
          .catch((error) => console.error("Error making POST request:", error));
      };

  return (
    <div className="model_box">
      <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>New Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
          <div className="form-group mt-3">
              <label>Image Source:</label>
              <input
                type="text"
                className="form-control"
                name="imgSrc"
                value={formData.imgSrc}
                onChange={handleChange}
                placeholder="Image Source (URL)"
                required
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  handleImageFileChange(e); // Pass the event object
                  handleImageUploadClick(e); // Pass the event object to trigger the upload
                }}
                required
              />
          </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="imgAlt"
                value={formData.imgAlt}
                onChange={handleChange}
                placeholder="Image Alt Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Title"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="subtitle"
                value={formData.subtitle}
                onChange={handleChange}
                placeholder="Subtitle"
                required
              />
            </div>
            <div className="form-group mt-3">
              <textarea
                className="form-control"
                name="text"
                value={formData.text}
                onChange={handleChange}
                placeholder="Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonText"
                value={formData.buttonText}
                onChange={handleChange}
                placeholder="Button Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonRedirectURL"
                value={formData.buttonRedirectURL}
                onChange={handleChange}
                placeholder="Button Redirect URL"
                required
              />
            </div>
            <button type="submit" className="btn btn-success mt-4">
              Adaugare
            </button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Inchidere
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default NewBlogPostModal;
