import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
 
const NewBlogPostModal = ({ show, handleClose, setData }) => {
  const [formData, setFormData] = useState({
    imgSrc: '',
    selectedImage: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };
 
  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setFormData({
      ...formData,
      selectedImage,
    });
  };

  const handleImageUpload = () => {
    if (formData.selectedImage) {
      const formDataUpload = new FormData();
      formDataUpload.append('image', formData.selectedImage);

      fetch('http://localhost:5188/api/ResourcesBlogPosts/ManageUploadedImages', {
        method: 'POST',
        body: formDataUpload,
      })
        .then((response) => response.json())
        .then((data) => {
          // Handle the response from the image upload API
          setFormData({
            ...formData,
            imgSrc: data.imgSrc, // Assuming the response has an imgSrc field with the image URL
          });
        })
        .catch((error) => {
          console.error('Image upload error:', error);
        });
    }
  };
     
 
    const handleSubmit = (e) => {
        e.preventDefault();
        // Define your API endpoint
        const apiUrl = "http://localhost:5188/api/ResourcesBlogPosts/InserareNewBlogPost"; // Replace with your actual endpoint
     
        // Prepare the request body based on your data structure
        const requestData = {
          imgSrc: formData.imgSrc,
          imgAlt: formData.imgAlt,
          title: formData.title,
          subtitle: formData.subtitle,
          text: formData.text,
          buttonText: formData.buttonText,
          buttonRedirectURL: formData.buttonRedirectURL,
        };
     
        // Make a POST request to your API endpoint
        fetch(apiUrl, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(requestData),
        })
          .then((response) => {
            if (response.ok) {
              // Refresh the data or perform other actions after a successful POST
              fetch("http://localhost:5188/api/ResourcesBlogPosts")
                .then((response) => response.json())
                .then((updatedData) => {
                  setData(updatedData);
                  handleClose();
                })
                .catch((error) => console.error("Error fetching updated data:", error));
            } else {
              // Handle errors here
            }
          })
          .catch((error) => console.error("Error making POST request:", error));
      };
 
  return (
    <div className="model_box">
      <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>New Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
          <div className="form-group mt-3">
        <label>Upload Image:</label>
        <input
          type="file"
          accept=".jpg, .jpeg, .png, .gif"
          onChange={handleImageChange}
        />
        <button onClick={handleImageUpload}>Upload</button>
      </div>

      <div className="form-group mt-3">
        <label>Image Source:</label>
        <input
          type="text"
          className="form-control"
          name="imgSrc"
          value={formData.imgSrc}
          onChange={handleChange}
          placeholder="Image Source (URL)"
          required
        />
      </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="imgAlt"
                value={formData.imgAlt}
                onChange={handleChange}
                placeholder="Image Alt Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Title"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="subtitle"
                value={formData.subtitle}
                onChange={handleChange}
                placeholder="Subtitle"
                required
              />
            </div>
            <div className="form-group mt-3">
              <textarea
                className="form-control"
                name="text"
                value={formData.text}
                onChange={handleChange}
                placeholder="Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonText"
                value={formData.buttonText}
                onChange={handleChange}
                placeholder="Button Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonRedirectURL"
                value={formData.buttonRedirectURL}
                onChange={handleChange}
                placeholder="Button Redirect URL"
                required
              />
            </div>
            <button type="submit" className="btn btn-success mt-4">
              Adaugare
            </button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Inchidere
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
 
export default NewBlogPostModal;
