import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./AdminPage.css"
import { Button, Modal } from "react-bootstrap";
import Navbar from '../../components/NavbarComponent/Navbar';
import LogoutConfirmationModal from "./LogoutConfirmationModal";
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

function AdminPage() {
  const location = useLocation();
  const { numeAngajat, prenumeAngajat } = location.state || {};
  const navigate = useNavigate();
  const [show, setShow] = useState(false);
  const [data, setData] = useState([]);
  const [showLogoutConfirmation, setShowLogoutConfirmation] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [showUpdateConfirmation, setShowUpdateConfirmation] = useState(false);
  const [updateCnp, setUpdateCnp] = useState(''); // State variable to store CNP to be updated
  const [updateData, setUpdateData] = useState({
    nume: '',
    prenume: '',
    pozitie: '',
    departament: '',
    salariu: 0,
    nivelStudii: '',
    cnp: '',
    username: '',
    password: '',
    isAdmin: false,
    isAgentVanzari: false,
    isContabil: false,
    isLucratorService: false,
  });

    const [formData, setFormData] = useState({
    nume: "",
    prenume: "",
    pozitie: "",
    departament: "",
    salariu: 0,
    nivelStudii: "",
    cnp: "",
    username: "",
    password: "",
    isAdmin: false,
    isAgentVanzari: false,
    isContabil: false,
    isLucratorService: false,
  });

  const handleClose = () => {
    setShow(false);
    setFormData({
      nume: "",
      prenume: "",
      pozitie: "",
      departament: "",
      salariu: 0,
      nivelStudii: "",
      cnp: "",
      username: "",
      password: "",
      isAdmin: false,
      isAgentVanzari: false,
      isContabil: false,
      isLucratorService: false,
    });
  };

  const [deleteCnp, setDeleteCnp] = useState(""); // CNP to delete
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [searchCNP, setSearchCNP] = useState(""); // State variable for the search input

  const handleShow = () => setShow(true);

  const handleLogout = () => {
    setShowLogoutConfirmation(true);
  };

  const handleConfirmLogOut = () => {
    setShowLogoutConfirmation(false);
    navigate('/');
  };

  const handleCancelLogOut = () => {
    setShowLogoutConfirmation(false);
  };

  const handleSearchInputChange = (e) => {
    // Update the search input value when the input changes
    setSearchCNP(e.target.value);
  };

  const filteredData = data.filter((employee) =>
    employee.cnp.includes(searchCNP)
  );

  const handleChange = (e) => {
    const { name, value } = e.target;
    // For checkboxes, we'll convert the labels back to boolean values
    if (name === "isAdmin" || name === "isAgentVanzari" || name === "isContabil" || name === "isLucratorService") {
      setFormData({ ...formData, [name]: !formData[name] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Make a POST request to your API endpoint
    fetch("http://localhost:5036/api/DateCompleteAngajat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Refresh the employee list after successful POST
          fetch("http://localhost:5036/api/DateCompleteAngajat")
            .then((response) => response.json())
            .then((updatedData) => {
              setData(updatedData); // Update the data state
              handleClose(); // Close the modal
            })
            .catch((error) => console.error("Error fetching updated data:", error));
        } else {
          // Handle errors here
        }
      })
      .catch((error) => console.error("Error making POST request:", error));
  };

  useEffect(() => {
    // Fetch data from your API endpoint
    fetch("http://localhost:5036/api/DateCompleteAngajat")
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleDeleteConfirmation = (cnp) => {
    // Set the CNP to delete and show the confirmation modal
    setDeleteCnp(cnp);
    setShowDeleteConfirmation(true);
  };

  const handleOpenUpdateModal = (angajatData) => {
    setShowUpdateModal(true);
    setUpdateData(angajatData);
  };

  const handleUpdateConfirmation = (cnp) => {
    // Set the CNP to delete and show the confirmation modal
    setUpdateCnp(cnp);
    setShowUpdateConfirmation(true);
  };
  
  const handleDelete = () => {
    // Make a DELETE request to your API endpoint using the stored cnpToDelete
    fetch(`http://localhost:5036/api/DateCompleteAngajat/${deleteCnp}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful deletion here, e.g., refresh the employee list
          // Fetch the updated data and update the 'data' state
          fetch("http://localhost:5036/api/DateCompleteAngajat")
            .then((response) => response.json())
            .then((updatedData) => setData(updatedData))
            .catch((error) => console.error("Error fetching updated data:", error));
          setShowDeleteConfirmation(false); // Close the delete confirmation modal
        } else {
          // Handle errors here
          console.error("Error deleting employee with CNP:", deleteCnp);
        }
      })
      .catch((error) => console.error("Error making DELETE request:", error));
  };

  const handleUpdate = () => {
    // Make a PUT request to your API endpoint using the updateData
    fetch(`http://localhost:5036/api/Login/UpdateDateGeneraleAngajat/${updateData.cnp}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updateData),
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful update here, e.g., refresh the employee list
          // Fetch the updated data and update the 'data' state
          fetch('http://localhost:5036/api/DateCompleteAngajat')
            .then((response) => response.json())
            .then((updatedData) => {
              setData(updatedData);
              setShowUpdateModal(false); // Close the update modal
            })
            .catch((error) => console.error('Error fetching updated data:', error));
        } else {
          // Handle errors here
          console.error('Error updating employee with CNP:', updateData.cnp);
        }
      })
      .catch((error) => console.error('Error making PUT request:', error));
  };

  return (

    <div>
      <nav id="navbar">
      <div className="wrapper">
        <div className="logo">
          <a href="#">
            <img
            src="https://logos-world.net/wp-content/uploads/2022/07/Hertz-Logo.png"
            alt="Hertz Logo"
            style={{ maxWidth: '100px', maxHeight: '50px' }}
          />
          </a>
        </div>
        <span className="welcome-label">Welcome back, {numeAngajat} {prenumeAngajat}!</span>
        <input type="radio" name="slider" id="menu-btn" />
        <input type="radio" name="slider" id="close-btn" />
        <ul className="nav-links">
          <label htmlFor="close-btn" className="btn close-btn">
            <i className="fas fa-times"></i>
          </label>
          <li>
            <a href="">Angajati</a>
          </li>
          <li>
            <a href="#">About</a>
          </li>
            <input type="checkbox" id="showMega" />
            <li>
            <a href="#">Feedback</a>
          </li>
          <li>
            <a className="LogoutButton" onClick={handleLogout}>
              <i className="fas fa-sign-out-alt"></i> Log Out
            </a>
          </li>
        </ul>
        <label htmlFor="menu-btn" className="btn menu-btn">
          <i className="fas fa-bars"></i>
        </label>
      </div>
    </nav>
    <LogoutConfirmationModal
        show={showLogoutConfirmation}
        onHide={handleCancelLogOut}
        onConfirmLogout={handleConfirmLogOut}
      />

      <div className="container" style={{ marginTop: '160px' }}>
      <div className="crud shadow-lg p-3 mb-5 mt-5 bg-body rounded">
        <div className="row">
          <div className="col-sm-3 mt-5 mb-4 text-gred">
            <div className="search">
              <form className="form-inline">
                <input
                  className="form-control mr-sm-2"
                  type="search"
                  placeholder="CNP"
                  aria-label="Search"
                  value={searchCNP}
                  onChange={handleSearchInputChange}
                />
              </form>
            </div>
          </div>
          <div className="col-sm-3 offset-sm-2 mt-5 mb-4 text-gred">
            <h2 style={{ color: "black" }}>
              <b>Angajati</b>
            </h2>
          </div>
          <div className="col-sm-3 offset-sm-1 mt-5 mb-4 text-gred">
            <Button variant="primary" onClick={handleShow}>
              Adaugare angajat
            </Button>
          </div>
        </div>
        <div className="row">
        <div className="table-responsive">          
        <table className="table table-striped table-hover table-bordered custom-table">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nume</th>
                <th>Prenume</th>
                <th>Pozitie</th>
                <th>Departament</th>
                <th>Salariu</th>
                <th>Nivel Studii</th>
                <th>CNP</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
            {filteredData.map((employee) => (
                  <tr key={employee.id}>
                    <td>{employee.id}</td>
                    <td>{employee.nume}</td>
                    <td>{employee.prenume}</td>
                    <td>{employee.pozitie}</td>
                    <td>{employee.departament}</td>
                    <td>{employee.salariu}</td>
                    <td>{employee.nivelStudii}</td>
                    <td>{employee.cnp}</td>
                    <td>{employee.username}</td>
                    <td>
                    <a className="edit" title="Edit" data-toggle="tooltip" onClick={() => handleUpdateConfirmation(employee.cnp)}>
                      <i className="material-icons">&#xE254;</i>
                    </a>
                    <a className="delete" title="Delete" data-toggle="tooltip" onClick={() => handleDeleteConfirmation(employee.cnp)}>
                      <i className="material-icons">&#xE872;</i>
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteConfirmation} onHide={() => setShowDeleteConfirmation(false)} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>Sunteti sigur ca doriti sa eliminati acest angajat?</Modal.Title>
        </Modal.Header>
        <Modal.Footer>
          <Button variant="secondary" className="btn-modal" onClick={() => setShowDeleteConfirmation(false)}>
            Revenire
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Eliminare
          </Button>
        </Modal.Footer>
      </Modal>
      </div>

      <div>
      {/* Update Modal */}
      <Modal show={showUpdateModal} onHide={() => setShowUpdateModal(false)} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>Actualizare angajat</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleUpdate}>
            {/* Add input fields for all the data */}
            {/* You can use the `updateData` state to pre-fill values */}
            {/* Example for 'nume' field */}
            <div className="form-group">
              <input
                type="text"
                className="form-control"
                name="nume"
                value={updateData.nume}
                onChange={(e) => setUpdateData({ ...updateData, nume: e.target.value })}
                placeholder="Nume"
                required
              />
            </div>
            {/* Repeat similar input fields for other data */}
            {/* ... */}
            <button type="submit" className="btn btn-success mt-4">
              Actualizare
            </button>
          </form>
        </Modal.Body>
