import React, { useState } from "react";
import "./components/LoginComponent/LoginPage.css";

function LoginForm() {
    const [selectedOption, setSelectedOption] = useState(""); // State to keep track of the selected option
  
    // Function to handle dropdown selection
    const handleDropdownChange = (event) => {
      setSelectedOption(event.target.value);
    };
  
    return (
      <div className="container">
        <div className="screen">
          <div className="screen__content">
            <form className="login">
              <div className="login__field">
                <i className="login__icon fas fa-user"></i>
                <input
                  type="text"
                  className="login__input"
                  placeholder="Username"
                  style={{ color: 'yellow' }}
                />
              </div>
              <div className="login__field">
                <i className="login__icon fas fa-lock"></i>
                <input
                  type="password"
                  className="login__input"
                  placeholder="Password"
                  style={{ color: 'yellow' }}
                />
              </div>
              {/* Dropdown */}
              <div className="login__field">
              <label htmlFor="dropdown" className="yellow-label">Departament:</label>
              <select
                id="dropdown"
                className="login__dropdown"
                value={selectedOption}
                onChange={handleDropdownChange}
                style={{ backgroundColor: 'transparent', color: 'yellow' }}
              >
                <option value="option1" className="black-option">Management</option>
                <option value="option2" className="black-option">Vanzari</option>
                <option value="option3" className="black-option">Service</option>
                <option value="option4" className="black-option">Contabilitate</option>
              </select>
            </div>
              <button className="button login__submit">
                <span className="button__text">Log In Now</span>
                <i className="button__icon fas fa-chevron-right"></i>
              </button>
            </form>
          </div>
          <div className="screen__background">
            <span className="screen__background__shape screen__background__shape4"></span>
            <span className="screen__background__shape screen__background__shape2"></span>
          </div>
        </div>
      </div>
    );
  }
  
  export default LoginForm;
