import React, { useState, useEffect } from 'react';
import LogoutConfirmationModal from '../ModalsComponent/LogoutConfirmationModal';
import NewBlogPostModal from '../ModalsComponent/NewBlogPostModal';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const BlogPost = ({ post }) => (
  <div className="w3-card-4 w3-margin w3-white" key={post.id}>
    <img src={post.imgSrc} alt={post.imgAlt} style={{ width: '100%', height: '100%' }} />
    <div className="w3-container">
      <p></p>
      <h3><b>{post.title}</b></h3>
      <h5>{post.subtitle}<span className="w3-opacity"></span></h5>
    </div>

    <div className="w3-container">
      <p></p>
      <div dangerouslySetInnerHTML={{ __html: post.text }} /> {/* Use dangerouslySetInnerHTML */}
      <div className="w3-row">
        <div className="w3-col m8 s12">
          <p></p>
          <p>
            <button
              className="w3-button w3-padding-large w3-white w3-border"
              onClick={() => window.open(post.buttonRedirectURL, '_blank')}
            >
              <b>{post.buttonText}</b>
            </button>
          </p>
        </div>
        <div className="w3-col m4 w3-hide-small"></div>
      </div>
    </div>
  </div>
);

const initialFormData = {
  imgSrc: '',
  imgAlt: '',
  title: '',
  subtitle: '',
  text: '',
  buttonText: '',
  buttonRedirectURL: '',
};


function AdminHomePage() {
    const location = useLocation();
    const { numeAngajat, prenumeAngajat } = location.state || {};
    const navigate = useNavigate();
    const [showLogoutConfirmation, setShowLogoutConfirmation] = useState(false);
    const [showNewBlogPostModal, setShowNewBlogPostModal] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [employees, setEmployees] = useState([]);
    const [blogPosts, setBlogPosts] = useState([]);
    const [formData, setFormData] = useState(initialFormData);

    const handleChange = (e) => {
      const { name, value } = e.target;
    
      // Assuming you have a state variable named 'formData' and a corresponding 'setFormData' function
      setFormData({
        ...formData,
        [name]: value,
      });
    };
    
  const getAdminEmployees = () => {
    fetch('http://localhost:5036/api/DateCompleteAngajat')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        // Filter employees with isAdmin = true
        const isAdminEmployees = data.filter((employee) => employee.isAdmin);
        setEmployees(isAdminEmployees);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const getBlogPosts = () => {
    fetch('http://localhost:5188/api/ResourcesBlogPosts')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        setBlogPosts(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const handleCreateBlogPost = (newPostData) => {
    if (formData.selectedImage) {
      // Upload the image
      const formDataUpload = new FormData();
      formDataUpload.append('image', formData.selectedImage);
  
      fetch('http://localhost:5188/api/ResourcesBlogPosts/ManageUploadedImages', {
        method: 'POST',
        body: formDataUpload,
      })
        .then((response) => response.json())
        .then((data) => {
          // Handle the response from the image upload API
          const updatedData = {
            imgSrc: data.imgSrc, // Assuming the response has an imgSrc field with the image URL
            imgAlt: formData.imgAlt,
            title: formData.title,
            subtitle: formData.subtitle,
            text: formData.text,
            buttonText: formData.buttonText,
            buttonRedirectURL: formData.buttonRedirectURL,
          };
  
          // Define your API endpoint for creating a new blog post
          const apiUrl = "http://localhost:5188/api/ResourcesBlogPosts/InserareNewBlogPost"; // Replace with your actual endpoint
  
          // Make a POST request to create the new blog post
          fetch(apiUrl, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
          })
            .then((response) => {
              if (response.ok) {
                // If the POST request is successful, you can refresh the data or perform other actions
                fetch("http://localhost:5188/api/ResourcesBlogPosts")
                  .then((response) => response.json())
                  .then((newlyCreatedData) => {
                    // Handle the response from fetching updated data
                    setData(newlyCreatedData); // Assuming setData is a function to update your data
                    handleClose(); // Close the modal
                  })
                  .catch((error) => console.error("Error fetching updated data:", error));
              } else {
                // Handle errors here
              }
            })
            .catch((error) => console.error("Error making POST request:", error));
        })
        .catch((error) => {
          console.error('Image upload error:', error);
          // Handle image upload error
        });
    }
  };
  

  useEffect(() => {
    getBlogPosts();
  }, []); 

  useEffect(() => {
    getAdminEmployees();
  }, []);

  const handleLogout = () => {
    setShowLogoutConfirmation(true);
  };

  const handleConfirmLogOut = () => {
    setShowLogoutConfirmation(false);
    navigate('/');
  };

  const handleCancelLogOut = () => {
    setShowLogoutConfirmation(false);
  };
  
  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleShowNewBlogPostModal = () => {
    setShowNewBlogPostModal(true);
  };

  const handleCloseNewBlogPostModal = () => {
    setShowNewBlogPostModal(false);
  };
  
  return (
    <div>
    <div>
      <nav id="navbar">
      <div className="wrapper">
      <div className="logo">
          <a href="/adminHomePage" onClick={(e) => {
            e.preventDefault();
            navigate("/adminHomePage", {
              state: {numeAngajat: numeAngajat, prenumeAngajat: prenumeAngajat},
            });
          }}>
            <img
            src="https://logos-world.net/wp-content/uploads/2022/07/Hertz-Logo.png"
            alt="Hertz Logo"
            style={{ maxWidth: '100px', maxHeight: '50px' }}
          />
          </a>
        </div>
        <span className="welcome-label" style={{ marginLeft: '-240px' }}>
            Welcome back, {numeAngajat} {prenumeAngajat}!
        </span>        
        <input type="radio" name="slider" id="menu-btn" />
        <input type="radio" name="slider" id="close-btn" />
        <ul className="nav-links">
          <label htmlFor="close-btn" className="btn close-btn">
            <i className="fas fa-times"></i>
          </label>
            <li>
              <a href="#" onClick={handleShowNewBlogPostModal}>New Post</a>
            </li>
          <li>
            <a
              href="/angajatPage"
              onClick={(e) => {
                e.preventDefault(); // Prevent the default link behavior
                navigate('/angajatPage', {
                  state: { numeAngajat: numeAngajat, prenumeAngajat: prenumeAngajat },
                });
              }}
            >
              Angajati
            </a>
          </li>
          <li>
            <a href="">Stoc</a>
          </li>
            <input type="checkbox" id="showMega" />
            <li>
            <a href="">Furnizori</a>
          </li>
          <li>
            <a className="LogoutButton" onClick={handleLogout}>
              <i className="fas fa-sign-out-alt"></i> Log Out
            </a>
          </li>
        </ul>
        <label htmlFor="menu-btn" className="btn menu-btn">
          <i className="fas fa-bars"></i>
        </label>
      </div>
    </nav>
    
    <LogoutConfirmationModal
        show={showLogoutConfirmation}
        onHide={handleCancelLogOut}
        onConfirmLogout={handleConfirmLogOut}
      />

      <NewBlogPostModal
        show={showNewBlogPostModal}
        handleClose={handleCloseNewBlogPostModal}
        setData={handleCreateBlogPost}
      />    </div>
    

    <div className="w3-content" style={{ maxWidth: '1400px' }}>
      <header className="w3-container w3-center w3-padding-32">
      <h1 style={{ marginTop: '60px' }}><b>Hertz</b></h1>
        <p>Welcome to your work space. We are glad that you are here.</p>
      </header>
      <div className="w3-row">
        <div className="w3-col l8 s12">
          {/* Blog entries */}
            {/* Blog entry content */}
            {/* Blog entry content is postes dynamically*/}

            {blogPosts.map((post) => (
              <BlogPost post={post} key={post.id} />
            ))}

        </div>
        <div className="w3-col l4">
          {/* Introduction menu */}
          {/* Introduction 1 */}
          <div className="w3-card w3-margin w3-margin-top">
        <img
          src="/images/Bajdechi Leonardo Picture.jpg"
          style={{ width: '100%' }}
          alt="Profile Image"
        />
        <div className="w3-container w3-white">
          <p></p>
          <h4><b>Leonardo Bajdechi</b></h4>
          <p>
            Software developer and creator of our work environment - the Hertz App. 
            He is the reason that all your tasks and resources are organized and protected, 
            within an easy-to-use application, dedicated to everyone and with one of the biggest communities in the car industry. 
            He loves working in a team, having fun with everyone, 
            learning every day, and helping others with anything they need.
          </p>
        </div>
        <p></p>
      </div>
      <hr />
          {/* Posts */}
          <div className="w3-card w3-margin">
      <div className="w3-container w3-padding">
        <h4>Meet the team</h4>
      </div>
      <ul className="w3-ul w3-hoverable w3-white">
        {employees.map((employee, index) => (
          <li className="w3-padding-16" key={index}>
            <img
              src="/images/Bajdechi Leonardo Picture.jpg"
              alt={`${employee.nume} ${employee.prenume}`}
              className="w3-left w3-margin-right"
              style={{ width: '50px' }}
            />
            <span className="w3-large">{`${employee.nume} ${employee.prenume}`}</span>
            <br />
            <span>{employee.pozitie}</span>
          </li>
        ))}
      </ul>
    </div>
          {/* Labels / tags */}
          <div class="w3-card w3-margin">
    <div class="w3-container w3-padding">
      <h4>Tags</h4>
    </div>
    <div class="w3-container w3-white">
    <p><span class="w3-tag w3-black w3-margin-bottom">Have fun</span> <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Relax</span> <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Love</span>
      <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Learn</span> <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Study</span> <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Cars</span>
      <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Communicate</span> <span class="w3-tag w3-light-grey w3-small w3-margin-bottom">Games</span>
    </p>
    </div>
  </div>
        </div>
      </div>
      <footer className="w3-container w3-dark-grey w3-padding-32 w3-margin-top">
      <a href="https://www.facebook.com/hertzro" target="_blank" rel="noopener noreferrer">
        <label className="w3-button w3-blue w3-padding-large w3-margin-bottom">
            Follow us on <i className="fa fa-facebook"></i>
        </label>
      </a>
      <a href="https://www.instagram.com/hertz.romania/" target="_blank" rel="noopener noreferrer">
        <label className="w3-button w3-padding-large w3-margin-bottom" style={{ backgroundColor: '#405DE6', color: 'white' }}>
            Follow us on <i className="fa fa-instagram" style={{ color: 'white' }}></i>
        </label>
        </a>
        <button className="w3-button w3-black w3-padding-large w3-margin-bottom">
            Team meetings coming soon. Stay tuned for many events.
          </button>
      </footer>
    </div>
    </div>
  );
}

export default AdminHomePage;
