import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

function DeleteConfirmationModal({ show, onHide, onConfirmDelete, blogPostId }) {
  return (
    <Modal show={show} onHide={onHide} backdrop="static" keyboard={false}>
      <Modal.Header closeButton>
        <Modal.Title>Sunteti sigur ca doriti sa stergeti aceasta postare?</Modal.Title>
      </Modal.Header>
      <Modal.Footer>
        <Button variant="secondary" className="btn-modal" onClick={onHide}>
          Revenire
        </Button>
        <Button variant="danger" onClick={() => onConfirmDelete(blogPostId)}>
          Eliminare
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default DeleteConfirmationModal;
