import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
 
const NewBlogPostModal = ({ show, handleClose, handleNewPostRequests, imgSrc }) => {
  const [formData, setFormData] = useState({
    selectedImage: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };
 
  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setFormData({
      ...formData,
      selectedImage,
    });
  };

  const handleImageUpload = (formData, handleClose) => {
    if (formData.selectedImage) {
      const formDataUpload = new FormData();
      formDataUpload.append('image', formData.selectedImage);
  
      fetch('http://localhost:5188/api/ResourcesBlogPosts/ManageUploadedImages', {
        method: 'POST',
        body: formDataUpload,
      })
        .then((response) => response.json())
        .then((data) => {
          // Handle the response from the image upload API
          const newImgSrc = data.imagePath; // Assuming the response has an imgSrc field with the image URL
  
          // Define your API endpoint for creating a new blog post
          const apiUrl = "http://localhost:5188/api/ResourcesBlogPosts/InserareNewBlogPost"; // Replace with your actual endpoint
  
          // Create a new blog post
          const updatedData = {
            imgSrc: newImgSrc,
            imgAlt: formData.imgAlt,
            title: formData.title,
            subtitle: formData.subtitle,
            text: formData.text,
            buttonText: formData.buttonText,
            buttonRedirectURL: formData.buttonRedirectURL,
          };
  
          fetch(apiUrl, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedData),
          })
            .then((response) => {
              if (response.ok) {
                // If the POST request is successful, trigger a page refresh
                window.location.reload();
              } else {
                // Handle errors here
              }
            })
            .catch((error) => console.error("Error making POST request:", error))
            .finally(() => {
              // Reset the form data and close the modal
              setFormData(initialFormData);
              handleClose();
            });
        })
        .catch((error) => {
          console.error('Image upload error:', error);
          // Handle image upload error
        });
    }
  };  
 
  return (
    <div className="model_box">
      <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>New Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
          <div className="form-group mt-3">
        <label>Upload Image:</label>
        <p></p>
        <input
          type="file"
          accept=".jpg, .jpeg, .png, .gif"
          onChange={handleImageChange}
        />
      </div>

      <div className="form-group mt-3" style={{ display: 'none' }}>
        <label>Image Source:</label>
        <input
          type="text"
          className="form-control"
          name="imgSrc"
          value={imgSrc}
          onChange={handleChange}
          placeholder="Image Source (URL)"
          required
        />
      </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="imgAlt"
                value={formData.imgAlt}
                onChange={handleChange}
                placeholder="Image Alt Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Title"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="subtitle"
                value={formData.subtitle}
                onChange={handleChange}
                placeholder="Subtitle"
                required
              />
            </div>
            <div className="form-group mt-3">
              <textarea
                className="form-control"
                name="text"
                value={formData.text}
                onChange={handleChange}
                placeholder="Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonText"
                value={formData.buttonText}
                onChange={handleChange}
                placeholder="Button Text"
                required
              />
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="buttonRedirectURL"
                value={formData.buttonRedirectURL}
                onChange={handleChange}
                placeholder="Button Redirect URL"
                required
              />
            </div>
            <button
      type="submit"
      className="btn btn-success mt-4"
      onClick={() => handleNewPostRequests(formData, handleNewPostRequests, handleClose)}
    >
      Adaugare
    </button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Inchidere
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
 
export default NewBlogPostModal;
