import React, { useState } from "react";
import "./LoginPage.css";

function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [selectedDepartment, setSelectedDepartment] = useState("Management"); // Default to Management

  // Function to handle username input
  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  // Function to handle password input
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  // Function to handle dropdown selection
  const handleDropdownChange = (event) => {
    setSelectedDepartment(event.target.value);
  };

  // Function to handle form submission
  const handleFormSubmit = async (event) => {
    event.preventDefault();

    // Create an object with the data to send to the backend
    const requestData = {
      username,
      password,
      IsAdmin: selectedDepartment === "Management",
      IsAgentVanzari: selectedDepartment === "Vanzari",
      IsLucratorService: selectedDepartment === "Service",
      IsContabil: selectedDepartment === "Contabilitate",
    };

    // Send the requestData object to your backend using fetch or any other HTTP client library

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestData),
      });

      if (response.ok) {
        // Authentication successful
        // Handle success logic here (e.g., redirect the user)
      } else {
        // Authentication failed
        // Handle error logic here (e.g., display an error message)
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  };

  return (
    <div className="container">
      <div className="screen">
        <div className="screen__content">
          <form className="login" onSubmit={handleFormSubmit}>
            <div className="login__field">
              <i className="login__icon fas fa-user"></i>
              <input
                type="text"
                className="login__input"
                placeholder="Username"
                value={username}
                onChange={handleUsernameChange}
                style={{ color: 'yellow' }}
                required
              />
            </div>
            <div className="login__field">
              <i className="login__icon fas fa-lock"></i>
              <input
                type="password"
                className="login__input"
                placeholder="Password"
                value={password}
                onChange={handlePasswordChange}
                style={{ color: 'yellow' }}
                required
              />
            </div>
            {/* Dropdown */}
            <div className="login__field">
              <label htmlFor="dropdown" className="yellow-label">Departament:</label>
              <select
                id="dropdown"
                className="login__dropdown"
                value={selectedDepartment}
                onChange={handleDropdownChange}
                style={{ backgroundColor: 'transparent', color: 'yellow' }}
              >
                <option value="Management" className="black-option">Management</option>
                <option value="Vanzari" className="black-option">Vanzari</option>
                <option value="Service" className="black-option">Service</option>
                <option value="Contabilitate" className="black-option">Contabilitate</option>
              </select>
            </div>
            <button type="submit" className="button login__submit">
              <span className="button__text">Log In Now</span>
              <i className="button__icon fas fa-chevron-right"></i>
            </button>
          </form>
        </div>
        <div className="screen__background">
          <span className="screen__background__shape screen__background__shape4"></span>
          <span className="screen__background__shape screen__background__shape2"></span>
        </div>
      </div>
    </div>
  );
}

export default LoginForm;
