import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./AdminPage.css"
import { Button, Modal } from "react-bootstrap";
import NavbarComponent from './Navbar';

function AdminPage() {
  const [show, setShow] = useState(false);
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({
    nume: "",
    prenume: "",
    pozitie: "",
    departament: "",
    salariu: 0,
    nivelStudii: "",
    cnp: "",
    username: "",
    password: "",
    isAdmin: false,
    isAgentVanzari: false,
    isContabil: false,
    isLucratorService: false,
  });

  const handleClose = () => {
    setShow(false);
    setFormData({
      nume: "",
      prenume: "",
      pozitie: "",
      departament: "",
      salariu: 0,
      nivelStudii: "",
      cnp: "",
      username: "",
      password: "",
      isAdmin: false,
      isAgentVanzari: false,
      isContabil: false,
      isLucratorService: false,
    });
  };

  const [deleteCnp, setDeleteCnp] = useState(""); // CNP to delete
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [searchCNP, setSearchCNP] = useState(""); // State variable for the search input

  const handleShow = () => setShow(true);

  const handleSearchInputChange = (e) => {
    // Update the search input value when the input changes
    setSearchCNP(e.target.value);
  };

  const filteredData = data.filter((employee) =>
    employee.cnp.includes(searchCNP)
  );

  const handleChange = (e) => {
    const { name, value } = e.target;
    // For checkboxes, we'll convert the labels back to boolean values
    if (name === "isAdmin" || name === "isAgentVanzari" || name === "isContabil" || name === "isLucratorService") {
      setFormData({ ...formData, [name]: !formData[name] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Make a POST request to your API endpoint
    fetch("http://localhost:5036/api/DateCompleteAngajat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Refresh the employee list after successful POST
          fetch("http://localhost:5036/api/DateCompleteAngajat")
            .then((response) => response.json())
            .then((updatedData) => {
              setData(updatedData); // Update the data state
              handleClose(); // Close the modal
            })
            .catch((error) => console.error("Error fetching updated data:", error));
        } else {
          // Handle errors here
        }
      })
      .catch((error) => console.error("Error making POST request:", error));
  };

  useEffect(() => {
    // Fetch data from your API endpoint
    fetch("http://localhost:5036/api/DateCompleteAngajat")
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleDeleteConfirmation = (cnp) => {
    // Set the CNP to delete and show the confirmation modal
    setDeleteCnp(cnp);
    setShowDeleteConfirmation(true);
  };
  
  const handleDelete = () => {
    // Make a DELETE request to your API endpoint using the stored cnpToDelete
    fetch(`http://localhost:5036/api/DateCompleteAngajat/${deleteCnp}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful deletion here, e.g., refresh the employee list
          // Fetch the updated data and update the 'data' state
          fetch("http://localhost:5036/api/DateCompleteAngajat")
            .then((response) => response.json())
            .then((updatedData) => setData(updatedData))
            .catch((error) => console.error("Error fetching updated data:", error));
          setShowDeleteConfirmation(false); // Close the delete confirmation modal
        } else {
          // Handle errors here
          console.error("Error deleting employee with CNP:", deleteCnp);
        }
      })
      .catch((error) => console.error("Error making DELETE request:", error));
  };

  return (
    <div className="container">
      <NavbarComponent></NavbarComponent>
      <div className="crud shadow-lg p-3 mb-5 mt-5 bg-body rounded">
        <div className="row">
          <div className="col-sm-3 mt-5 mb-4 text-gred">
            <div className="search">
              <form className="form-inline">
                <input
                  className="form-control mr-sm-2"
                  type="search"
                  placeholder="CNP"
                  aria-label="Search"
                  value={searchCNP}
                  onChange={handleSearchInputChange}
                />
              </form>
            </div>
          </div>
          <div className="col-sm-3 offset-sm-2 mt-5 mb-4 text-gred">
            <h2 style={{ color: "black" }}>
              <b>Angajati</b>
            </h2>
          </div>
          <div className="col-sm-3 offset-sm-1 mt-5 mb-4 text-gred">
            <Button variant="primary" onClick={handleShow}>
              Adaugare angajat
            </Button>
          </div>
        </div>
        <div className="row">
        <div className="table-responsive">
          <table className="table table-striped table-hover table-bordered custom-table">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nume</th>
                <th>Prenume</th>
                <th>Pozitie</th>
                <th>Departament</th>
                <th>Salariu</th>
                <th>Nivel Studii</th>
                <th>CNP</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
            {filteredData.map((employee) => (
                  <tr key={employee.id}>
                    <td>{employee.id}</td>
                    <td>{employee.nume}</td>
                    <td>{employee.prenume}</td>
                    <td>{employee.pozitie}</td>
                    <td>{employee.departament}</td>
                    <td>{employee.salariu}</td>
                    <td>{employee.nivelStudii}</td>
                    <td>{employee.cnp}</td>
                    <td>{employee.username}</td>
                    <td>
                    <a href="#" className="edit" title="Edit" data-toggle="tooltip">
                      <i className="material-icons">&#xE254;</i>
                    </a>
                    <a href="#" className="delete" title="Delete" data-toggle="tooltip" onClick={() => handleDeleteConfirmation(employee.cnp)}>
                      <i className="material-icons">&#xE872;</i>
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      <Modal show={showDeleteConfirmation} onHide={() => setShowDeleteConfirmation(false)} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>Sunteti sigur ca doriti sa eliminati acest angajat?</Modal.Title>
        </Modal.Header>
        <Modal.Footer>
          <Button variant="secondary" className="btn-modal" onClick={() => setShowDeleteConfirmation(false)}>
            Revenire
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Eliminare
          </Button>
        </Modal.Footer>
      </Modal>
      </div>

       {/* <!-- Model Box --> */}
       <div className="model_box">
          <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false}>
            <Modal.Header closeButton>
              <Modal.Title>Adaugare angajat</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    name="nume"
                    value={formData.nume}
                    onChange={handleChange}
                    placeholder="Nume"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="prenume"
                    value={formData.prenume}
                    onChange={handleChange}
                    placeholder="Prenume"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="pozitie"
                    value={formData.pozitie}
                    onChange={handleChange}
                    placeholder="Pozitie"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="departament"
                    value={formData.departament}
                    onChange={handleChange}
                    placeholder="Departament"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="number"
                    className="form-control"
                    name="salariu"
                    value={formData.salariu}
                    onChange={handleChange}
                    placeholder="Salariu"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="nivelStudii"
                    value={formData.nivelStudii}
                    onChange={handleChange}
                    placeholder="Nivel Studii"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="cnp"
                    value={formData.cnp}
                    onChange={handleChange}
                    placeholder="CNP"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    placeholder="Username"
                    required
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="password"
                    className="form-control"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Password"
                    required
                  />
                </div>
                <div className="form-check mt-3">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    name="isAdmin"
                    checked={formData.isAdmin}
                    onChange={handleChange}
                  />
                  <label className="form-check-label">Management</label>
                </div>
                <div className="form-check mt-3">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    name="isAgentVanzari"
                    checked={formData.isAgentVanzari}
                    onChange={handleChange}
                  />
                  <label className="form-check-label">Vanzari</label>
                </div>
                <div className="form-check mt-3">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    name="isContabil"
                    checked={formData.isContabil}
                    onChange={handleChange}
                  />
                  <label className="form-check-label">Contabilitate</label>
                </div>
                <div className="form-check mt-3">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    name="isLucratorService"
                    checked={formData.isLucratorService}
                    onChange={handleChange}
                  />
                  <label className="form-check-label">Service</label>
                </div>
                <button type="submit" className="btn btn-success mt-4">
                  Adaugare
                </button>
              </form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Inchidere
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      </div>
  );
}

export default AdminPage;
