import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './components/LoginComponent/LoginPage';
import AdminPage from './components/AdminComponent/AdminPage';
import ErrorModal from './components/LoginComponent/ErrorModal';
import NavbarComponent from './components/NavbarComponent/Navbar';

function App() {
  const [isErrorModalOpen, setIsErrorModalOpen] = useState(false); // State for the error modal

  const closeErrorModal = () => {
    setIsErrorModalOpen(false);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <LoginPage setIsErrorModalOpen={setIsErrorModalOpen} />
                {isErrorModalOpen && (
                  <ErrorModal onClose={closeErrorModal} />
                )}
              </>
            }
          />
          <Route path="/adminPage" element={<AdminPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
